mapper.update:
update board_mst
set boardSubject = #{boardSubject}, boardContent =  #{boardContent},
    boardUploadName = #{boardUploadName}, boardUploadSize = #{boardUploadSize}, boardUploadLocation = #{boardUploadLocation}
where boardIndex = #{boardIndex};

mapper.select:
SELECT COUNT(*) FROM ( SELECT b.boardIndex, b.boardSubject, u.userIndex, u.userId, u.userNick, b.boardRegDate, b.boardVisit, b.boardUploadName, b.boardUploadSize, b.boardUploadLocation, COUNT(r.replyIndex) AS replyCount,b.boardLikeCount FROM board_mst b JOIN user_mst u ON b.userIndex = u.userIndex LEFT JOIN reply_mst r ON b.boardIndex = r.boardIndex
where boardSubject like CONCAT('%', #{searchValue}, '%') where u.userNick like CONCAT('%', #{searchValue}, '%') where boardContent like CONCAT('%', #{searchValue}, '%') where( boardSubject like CONCAT('%', #{searchValue}, '%') or boardContent like CONCAT('%', #{searchValue}, '%')) where boardVisit >= #{searchValue} where boardLikeCount >= #{searchValue}
GROUP BY b.boardIndex, b.boardSubject, u.userId, b.boardRegDate, b.boardVisit, b.boardUploadName, b.boardUploadSize, b.boardUploadLocation ORDER BY boardIndex DESC ) AS subquery;

mapper.insert:
insert into board_like_mst (boardLikeId, boardIndex, userIndex, boardLike, boardBad)
 select 0,  #{boardIndex}, #{userIndex}, 1, 0
 from dual
 where NOT EXISTS(
 select 1 from board_like_mst where boardIndex = #{boardIndex} and userIndex = #{userIndex}
 )

mapper.update:
update reply_mst
set replyContent = #{replyContent}
where replyIndex = #{replyIndex}

mapper.delete:
DELETE outer_reply
FROM reply_mst AS outer_reply
JOIN (
SELECT replyGroup
FROM reply_mst
WHERE replyIndex = #{replyIndex}
AND boardIndex = #{boardIndex}
AND replySequence >= 1
) AS inner_criteria ON outer_reply.replyGroup = inner_criteria.replyGroup
WHERE outer_reply.boardIndex = #{boardIndex};

mapper.select:
select ifnull(MAX(replyGroup) + 1, 1) as maxCount
from reply_mst
where boardIndex = #{boardIndex}

select MAX(replySequence) + 1 as maxCount
from reply_mst
where boardIndex = #{boardIndex}
and replyGroup = #{replyGroup}
